mapper.select:
      select ifnull(a.turnOver,0) as turnOver ,ifnull(a.exportAmount,0) as exportAmount, ifnull(a.profit,0) as profit from (
        select
  (select sum(ifnull(total,0)) from form_buying_contract where 1= 1

      and dept_id = #{deptId}



       and unix_timestamp( sign_date )  >=  unix_timestamp(#{startDate})



      and  unix_timestamp( sign_date ) <= unix_timestamp(#{endDate})

  ) as turnOver,
  (select sum(ifnull(total_price,0)) from  form_selling_contract where 1=1

            and dept_id = #{deptId}



           and unix_timestamp( sign_date )  >=  unix_timestamp(#{startDate})



          and  unix_timestamp( sign_date ) <= unix_timestamp(#{endDate})


        ) as exportAmount,
   (select sum(ifnull(net_profit,0)) from settlement_list where 1=1


            and dept_id = #{deptId}



         and   unix_timestamp( settle_date )  >=  unix_timestamp(#{startDate})



          and  unix_timestamp( settle_date ) <= unix_timestamp(#{endDate})


        ) as  profit
from dual ) a

SELECT a.deptId, a.deptName, a.turnOver, b.exportAmount, c.profit FROM (SELECT deptId, deptName, SUM(IFNULL(turnOver, 0)) AS turnOver FROM (SELECT d.id AS deptId, d.name AS deptName, b.total AS turnOver FROM department d LEFT JOIN (SELECT * FROM form_buying_contract WHERE 1 = 1
and unix_timestamp( sign_date ) >= unix_timestamp(#{startDate})
and unix_timestamp( sign_date ) <= unix_timestamp(#{endDate})
) b ON b.dept_id = d.id) a GROUP BY deptId) a INNER JOIN (SELECT deptId, deptName, SUM(IFNULL(exportAmount, 0)) AS exportAmount FROM (SELECT d.id AS deptId, d.name AS deptName, b.total_price AS exportAmount FROM department d LEFT JOIN (SELECT * FROM form_selling_contract WHERE 1 = 1
and unix_timestamp( sign_date ) >= unix_timestamp(#{startDate})
and unix_timestamp( sign_date ) <= unix_timestamp(#{endDate})
) b ON b.dept_id = d.id) a GROUP BY deptId) b ON a.deptId = b.deptId INNER JOIN (SELECT deptId, deptName, SUM(IFNULL(profit, 0)) AS profit FROM (SELECT d.id AS deptId, d.name AS deptName, b.net_profit AS profit FROM department d LEFT JOIN (SELECT * FROM settlement_list WHERE 1 = 1
and unix_timestamp( settle_date ) >= unix_timestamp(#{startDate})
and unix_timestamp( settle_date ) <= unix_timestamp(#{endDate})
) b ON b.dept_id = d.id) a GROUP BY deptId) c ON c.deptId = b.deptId
