mapper.select:
      select ifnull(a.turnOver,0) as turnOver ,ifnull(a.exportAmount,0) as exportAmount, ifnull(a.profit,0) as profit from (
        select
  (select sum(ifnull(total,0)) from form_buying_contract where 1= 1

      and dept_id = #{deptId}



       and unix_timestamp( sign_date )  >=  unix_timestamp(#{startDate})



      and  unix_timestamp( sign_date ) <= unix_timestamp(#{endDate})

  ) as turnOver,
  (select sum(ifnull(total_price,0)) from  form_selling_contract where 1=1

            and dept_id = #{deptId}



           and unix_timestamp( sign_date )  >=  unix_timestamp(#{startDate})



          and  unix_timestamp( sign_date ) <= unix_timestamp(#{endDate})


        ) as exportAmount,
   (select sum(ifnull(net_profit,0)) from settlement_list where 1=1


            and dept_id = #{deptId}



         and   unix_timestamp( settle_date )  >=  unix_timestamp(#{startDate})



          and  unix_timestamp( settle_date ) <= unix_timestamp(#{endDate})


        ) as  profit
from dual ) a

select * from ( select t.deptId,t.deptName, sum(ifnull(t.turnOver,0)) as turnOver, sum(ifnull(t.exportAmount,0)) as exportAmount, sum(ifnull(t.profit,0)) as profit
from (
select d.id as deptId,d.name as deptName, b.total as turnOver, s.total_price as exportAmount, st.net_profit as profit from department d left join
( select * from form_buying_contract where 1 = 1
and unix_timestamp( sign_date ) >= unix_timestamp(#{startDate})
and unix_timestamp( sign_date ) <= unix_timestamp(#{endDate})
) b on b.dept_id = d.id
left join (select * from form_selling_contract where 1=1
and unix_timestamp( sign_date ) >= unix_timestamp(#{startDate})
and unix_timestamp( sign_date ) <= unix_timestamp(#{endDate})
) s on s.dept_id = d.id
left join ( select * from settlement_list where 1=1
and unix_timestamp( settle_date ) >= unix_timestamp(#{startDate})
and unix_timestamp( settle_date ) <= unix_timestamp(#{endDate})
) st on st.dept_id = d.id ) t group by deptId ) t1 where 1 = 1
and deptId = #{deptId}
